import React, { useState, useEffect } from "react";
import { useInterval } from "react-use";
import styled, { keyframes } from "styled-components";

function Slider() {
  const quotes1 = "This is good".split(",");
  const quotes2 = "Aman Khadka".split(",");
  const quotes3 = "Finally I'm learning programming".split(",");

  // const idioms = quotes.split("");

  const [count, setCount] = useState(0);
  const [play, setPlay] = useState(false);
  const [data, setData] = useState(quotes1);

  useInterval(() => {
    setCount(count + 1);
    console.log(count);

    if (count === 0) {
      setData(quotes1);
    }
    if (count === 1) {
      setData(quotes2);
    }
    if (count === 2) {
      setCount(0);
      setData(quotes3);
    }
  });

  // useEffect(() => {
  //   const timer = setTimeout(() => {
  //     setData(quotes1);
  //     setPlay(true);
  //   }, 4000);
  //   return () => clearTimeout(timer);
  // }, [data]);

  // console.log(quotes);

  return (
    <div>
      <Wrapper>
        {data.map((items, index) => (
          <span key={index}>{data}</span>
        ))}
      </Wrapper>
    </div>
  );
}

export default Slider;

const animation = keyframes`
0%{ opacity: 0;} 
25%{opacity:1; }
75%{opacity:1; }
100%{opacity: 0;} 
`;

const Wrapper = styled.span`
  display: inline-block;
  span {
    display: inline-block;
    opacity: 0;
    animation-name: ${animation};
    animation-duration: 6s;
    animation-fill-mode: forwards;
    animation-iteration-count: infinite;
    animation-timing-function: cubic-bezier(0.07, 0.82, 0.165, 1);
  }
`;
