import React, { useState, useEffect } from "react";
import { useInterval } from "react-use";
import styled, { keyframes } from "styled-components";

function Slider() {
  const random = [
    "This is good".split(","),
    "Aman Khadka".split(","),
    "Finally I'm learning programming".split(),
  ];
  //   const quote1 = "This is good".split();
  //   const quote2 = "Aman Khadka".split();
  //   const quote3 = "Finally I'm learning programming".split();

  const [quotes, setQuotes] = useState(random[0]);
  const [count, setCount] = useState(0);
  const [play, setPlay] = useState(false);

  console.log(random[0]);
  console.log(quotes);

  useInterval(
    () => {
      //Animate Quotes
      setQuotes(random[`${count}`]);
      setCount(count + 1);

      //Animate quote2, Looping
      if (count === 1) {
        setQuotes(random[`${count + 1}`]);
        setCount(count + 1);
      }

      //Animate quote3, Looping
      if (count === 2) {
        setCount(0);
        setQuotes(random[`${count + 1}`]);
      }
    },
    play ? 6000 : null
  );

  const quotesLength = random.length;

  useEffect(() => {
    const timer = setTimeout(() => {
      //Animate quote3 after timeout
      setQuotes(random[`${quotesLength - 1}`]);
      setPlay(true);
    }, 6500);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <Wrapper>
        {quotes.map((item, index) => (
          <span key={index}>{item}</span>
        ))}
      </Wrapper>
    </div>
  );
}

export default Slider;

const animation = keyframes`
0%{ opacity: 0; transform: translateX(-100px)}
25%{opacity:1; transform: translateX(0px)}
75%{opacity:1; transform:translateX(0px)}
100%{opacity: 0; transform: translateX(-100px)}
`;

const Wrapper = styled.span`
  display: inline-block;
  span {
    display: inline-block;
    opacity: 1;
    /* animation-name: ${animation};
    animation-duration: 6s;
    animation-fill-mode: forwards;
    animation-iteration-count: infinite;
    animation-timing-function: cubic-bezier(0.07, 0.82, 0.165, 1); */
  }
`;
