import React, { useState } from "react";
import { useInterval } from "react-use";
import Quotes from "./Idioms";

function Slider() {
  const quote1 = "This is good";
  const quote2 = "This is working";
  const quote3 = "Finally I'm learning programming";

  const [items, setItems] = useState(quote1);
  const [count, setCount] = useState(0);
  const [play, setPlay] = useState(false);

  useInterval(() => {
    //Animate Quotes
    setItems(quote1);
    setCount(count + 1);

    //Animate quote2, Looping
    setItems(quote2);
    setCount(count + 1);

    //Animate quote3, Looping
    setCount(0);
    setItems(quote3);
  });

  console.log(Quotes.length);
  return (
    <div>
      <div className="container-slider">
        {Quotes.map((text, key) => (
          <span key={key} className="idioms">
            {text[key]}
          </span>
        ))}
      </div>
    </div>
  );
}

export default Slider;
