import React from "react";
import styled from "styled-components";
import { useFormik } from "formik";
import * as yup from "yup";

function AdminWordsModal({ showModals, setShowModals }) {
  const validationSchema = yup.object({
    Designation: yup
      .string()
      .min(3)
      .required("Required and must not be less then 3 digit"),
    Description: yup
      .string()
      .min(10)
      .required("Required and must not be less than 10 digit"),
    Name: yup
      .string()
      .min(2)
      .required("Required and Name must not be less than 2 digit"),
  });

  const formik = useFormik({
    initialValues: { Designation: "", Description: "", Name: "" },
    validateOnBlur: true,
    validateSchema: validationSchema,
    // onSubmit,
  });
  return (
    <>
      {showModals ? (
        <FormContainer>
          <InputDescription type="text" />
        </FormContainer>
      ) : null}
    </>
  );
}

export default AdminWordsModal;

const FormContainer = styled.form`
  background-color: blue;
  display: flex;
  flex-direction: column;
  width: 40em;
  height: 30em;
  top: 15%;
  left: 30%;
  position: fixed;
`;

const InputDescription = styled.div`
  height: 15em;
  display: flex;
  flex-direction: column;
  padding: 10px;
  width: 20em;
  top: 35.5%;
  left: 39%;
  position: fixed;

  h5 {
    color: #3c574e;
    margin-top: 0;
    margin-bottom: 5px;
    left: -10%;
  }
`;
