import React, { useState, useRef, useEffect } from "react";
import styled from "styled-components";

function SchoolDetailsModal({ showModals, setShowModals }) {
  const [image, setImage] = useState("");
  const [preview, setPreview] = useState("");
  const [schoolName, setschoolName] = useState("");
  const [imageUrl, setimageUrl] = useState("");
  const finalInputRef = useRef();

  useEffect(() => {
    if (image) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(image);
    } else {
      setPreview(null);
    }
  }, [image]);

  const HandleImageInput = (event) => {
    event.preventDefault();
    finalInputRef.current.click();
    console.log(schoolName, imageUrl);

    setschoolName("");
    setimageUrl("");
  };
  return (
    <>
      {showModals ? (
        <DataEntryContainer showModal={showModals}>
          <Input
            type="text"
            placeholder="Please Enter Your School Name"
            onChange={(e) => setschoolName(e.target.value)}
          ></Input>
          <Input
            type="file"
            accept="images/*"
            display={"none"}
            ref={finalInputRef}
            onChange={(event) => {
              const imagefile = event.target.files[0];
              if (imagefile) {
                setImage(imagefile);
              } else {
                setImage(null);
              }
            }}
          ></Input>
          <Buttons
            padding={"7"}
            topspacing={38}
            leftspacing={37.2}
            onClick={HandleImageInput}
          >
            Add Logo
          </Buttons>
          <ImageContainer>
            <img
              src={preview}
              style={{ objectFit: "cover", height: "inherit" }}
            />
          </ImageContainer>

          <Buttons
            topspacing={82}
            leftspacing={38}
            padding={"10"}
            onClick={() => {
              alert("Added ");
            }}
          >
            Add Post
          </Buttons>
          <Buttons
            topspacing={82}
            leftspacing={68}
            padding={"10"}
            onClick={() => {
              setShowModals((prev) => !prev);
            }}
          >
            Cancel
          </Buttons>
        </DataEntryContainer>
      ) : null}
    </>
  );
}

export default SchoolDetailsModal;

const Input = styled.input`
  margin: 40px 0px 0px 30px;
  background-color: white;
  width: 20rem;
  height: 2rem;
  text-indent: 10px;
  outline: none;
  text-decoration: none;
  border: none;
  border-radius: 8px;
  display: ${(props) => props.display};
`;

const Buttons = styled.button`
  position: fixed;
  top: ${(props) => props.topspacing}%;
  left: ${(props) => props.leftspacing}%;
  padding: ${(props) => props.padding}px;
  /* padding: 10px 20px; */
  border-radius: 10px;
  cursor: pointer;
`;

const DataEntryContainer = styled.div`
  width: 35em;
  height: 30em;
  top: 20%;
  left: 35%;
  border-radius: 20px;
  position: fixed;
  background-color: black;
  z-index: 999;

  h3 {
    padding: 0 50px;
  }
`;

const ImageContainer = styled.div`
  position: absolute;
  top: 24%;
  left: 30%;
  height: 15vw;
  width: 15vw;
  background-color: white;
`;
