import React from "react";
import styled from "styled-components";
import { Formik, ErrorMessage, Field, Form } from "formik";
import * as yup from "yup";
import axios from "axios";
import { useHistory } from "react-router-dom";

function onsubmit(props) {
  const history = useHistory();

  history.push("/");
}

const Admins = () => (
  <>
    <Formik
      initialValues={{ email: "", password: "" }}
      validateOnBlur={true}
      onSubmit
      validationSchema={yup.object({
        email: yup.string().required("Required"),
        password: yup.string().required("Required"),
      })}
      onSubmit={onsubmit(values, setSubmitting)}
      // onSubmit={async (values, { setSubmitting }) => {
      //   const response = await axios
      //     .post("https://localhost:8080/login/login", values)
      //     .then(alert("Your login process is going on"), history.push("/"))
      //     .catch((err) => {
      //       if (err && err.response) setSubmitting(err.response.data.message);
      //     });
      // }}
      validationSchema={yup.object().shape({
        email: yup.string().email().required("Required"),
        email: yup.string().required("Required"),
      })}
    >
      {(props) => {
        const {
          values,
          touched,
          errors,
          isSubmitting,
          handleChange,
          handleBlur,
          handleSubmit,
        } = props;
        return (
          <>
            <FormContainer onSubmit={handleSubmit}>
              <Text>Welcome To Admin Panel</Text>
              <EmailPassword>
                <h5>
                  Username{" "}
                  {errors.email && touched.email && (
                    <ErrorText>{errors.email}</ErrorText>
                  )}
                </h5>
                <Input
                  name="email"
                  type="text"
                  placeholder="Enter your Username"
                  value={values.email}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  className={errors.email && touched.email && "error"}
                />

                <h5>
                  Password{" "}
                  {errors.email && touched.email && (
                    <ErrorText>{errors.email}</ErrorText>
                  )}
                </h5>
                <Input
                  name="password"
                  type="password"
                  placeholder="Enter your Password"
                  value={values.password}
                  onChange={handleChange}
                  onBlur={handleBlur}
                  className={errors.password && touched.password && "error"}
                />
              </EmailPassword>
              <SubmitButton type="submit">Submit</SubmitButton>
            </FormContainer>
          </>
        );
      }}
    </Formik>
  </>
);

export default Admins;

const ErrorText = styled.label`
  margin-top: 0;
  margin-bottom: 5px;
  color: red;
  text-decoration: none;
  font-weight: 500;
`;

const Text = styled.div`
  color: #3c574e;
  align-items: center;
  justify-content: center;
  display: flex;
  font-size: 1.5rem;
  font-weight: bold;
  text-decoration: underline;
`;

const FormContainer = styled.form`
  background-color: #020035;
  display: flex;
  flex-direction: column;
  width: 30em;
  height: 20em;
  padding: 20px;
  top: 50%;
  left: 50%;
  margin-left: -15em;
  margin-top: -9em;
  position: fixed;
  border-radius: 20px;
`;

const EmailPassword = styled.div`
  height: 15em;
  display: flex;
  flex-direction: column;
  padding: 10px;
  width: 20em;
  top: 35.5%;
  left: 39%;
  position: fixed;

  h5 {
    color: #3c574e;
    margin-top: 0;
    margin-bottom: 5px;
    left: -10%;
  }
`;

export const SubmitButton = styled.button`
  top: 12em;
  left: 20em;
  position: relative;
  padding: 11px 1.6em;
  width: 7em;
  color: #fff;
  font-size: 15px;
  font-weight: 600;
  border: none;
  border-radius: 100px 100px 100px 100px;
  cursor: pointer;
  transition: all, 240ms ease-in-out;
  background: rgba(241, 196, 15, 1);
  background: linear-gradient(
    58deg,
    rgba(241, 196, 15, 1) 20%,
    rgba(243, 172, 18, 1) 100%
  );
  &:focus {
    outline: none;
  }
  &:hover {
    filter: brightness(1.03);
  }
  &:disabled {
    filter: contrast(0.7);
  }
`;

export const Input = styled.input`
  margin-bottom: 10px;
  width: 20em;
  height: 30px;
  justify-content: center;
  align-items: center;
  margin-left: 20px;
  border-radius: 15px;
  outline: none;
  border: 1px solid rgba(200, 200, 200, 0.03);
  padding: 0 10px;
  transition: all, 200ms ease-in-out;
  box-sizing: border-box;
  border-bottom: 1.4px solid transparent;

  &:focus {
    outline: none;
    border-bottom: 2px solid #111;
  }
  &:not(:last-of-type) {
    border-bottom: 1.4px solid rgba(200, 200, 200, 0.4);
  }
`;
