import React, { useState } from "react";
import styled from "styled-components";
import { useFormik } from "formik";
import * as yup from "yup";

function AdminWordsModal({ showModals, setShowModals }) {
  const [designationplaceholder, setDesignationPlaceHolder] = useState(
    "Please Enter Your Designation"
  );
  const validationSchema = yup.object({
    Designation: yup
      .string()
      .required("Required and must not be less then 3 digit"),
    Description: yup
      .string()
      .min(10)
      .required("Required and must not be less than 10 digit"),
    Name: yup
      .string()
      .min(2)
      .required("Required and Name must not be less than 2 digit"),
  });

  const onSubmit = (values) => {
    console.log(values);
  };

  const formik = useFormik({
    initialValues: { Designation: "", Description: "", Name: "" },
    validateOnBlur: true,
    validateSchema: validationSchema,
    onSubmit,
  });

  return (
    <>
      {showModals ? (
        <FormContainer onSubmit={formik.handleSubmit}>
          {/* {formik.errors.Designation &&
            formik.touched.Designation &&
            setDesignationPlaceHolder(formik.errors.Designation)}
          {designationplaceholder} */}

          {/* {designationplaceholder} */}
          <InputField>
            <LabelText> Designation = </LabelText>
            {formik.errors.Designation && formik.touched.Designation && (
              <h1>{formik.errors.Designation}</h1>
            )}
            <InputDescription
              name="Designation"
              type="text"
              placeholder={designationplaceholder}
              width={25}
              height={2.5}
              value={formik.values.Designation}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              className={
                formik.errors.Designation &&
                formik.touched.Designation &&
                "errors"
              }
            ></InputDescription>
          </InputField>
          <button type="submit">Submit</button>
        </FormContainer>
      ) : null}
    </>
  );
}

export default AdminWordsModal;

const FormContainer = styled.form`
  background-color: blue;
  display: flex;
  flex-direction: column;
  width: 40em;
  height: 30em;
  top: 15%;
  left: 30%;
  position: fixed;
`;

const InputField = styled.div`
  display: flex;
  flex-direction: row;
  margin: 5px;
`;

const InputDescription = styled.input`
  margin-top: 10px;
  margin-bottom: 10px;
  width: ${(props) => props.width}em;
  height: ${(props) => props.height}em;
  /* width: 20em;
  height: 30px; */
  justify-content: center;
  align-items: center;
  margin-left: 20px;
  border-radius: 15px;
  outline: none;
  border: 1px solid rgba(200, 200, 200, 0.03);
  padding: 0 10px;
  transition: all, 200ms ease-in-out;
  box-sizing: border-box;
  border-bottom: 1.4px solid transparent;

  &:focus {
    outline: none;
    border-bottom: 2px solid #111;
  }
  &:not(:last-of-type) {
    border-bottom: 1.4px solid rgba(200, 200, 200, 0.4);
  }
`;

const LabelText = styled.label`
  display: flex;
  flex-direction: column;
  font-size: 1rem;
  color: red;
  justify-content: center;
  margin-left: 10px;
`;
