import React, { useState, useEffect } from "react";
import { useInterval } from "react-use";
import styled, { keyframes } from "styled-components";

function Slider() {
  const quotes = [
    "This is good",
    "Aman Khadka",
    "Finally I'm learning programming",
  ];
  const quote1 = "This is good".split();
  const quote2 = "Aman Khadka".split();
  const quote3 = "Finally I'm learning programming".split();

  useEffect(() => {
    const timer = setTimeout(() => {
      //Animate quote3 after timeout
      setQuotes(quote1);
      setQuotes(quote2);
      setQuotes(quote3);
      setPlay(true);
    }, 6000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <div>
      <Wrapper></Wrapper>
    </div>
  );
}

export default Slider;

const animation = keyframes`
0%{ opacity: 0;} 
25%{opacity:1; }
75%{opacity:1; }
100%{opacity: 0;} 
`;

const Wrapper = styled.span`
  display: inline-block;
  span {
    display: inline-block;
    opacity: 0;
    animation-name: ${animation};
    animation-duration: 6s;
    animation-fill-mode: forwards;
    animation-iteration-count: infinite;
    animation-timing-function: cubic-bezier(0.07, 0.82, 0.165, 1);
  }
`;
