import React from "react";
import styled from "styled-components";

function Modals({ showModals, setShowModals }) {
  return (
    <>
      {showModals ? (
        <DataEntryContainer showModal={showModals}>
          <Buttons
            spacing={"20"}
            onClick={() => {
              alert("You can view this !");
              //   setShowModals((prev) => !prev);
            }}
          >
            Okay
          </Buttons>
          <View
            spacing={"10"}
            onClick={() => {
              alert("You can view this !");
              //   setShowModals((prev) => !prev);
            }}
          >
            Okay
          </View>
          <View
            spacing={"55"}
            onClick={() => {
              alert("You can view this !");
              //   setShowModals((prev) => !prev);
            }}
          >
            Okay
          </View>
        </DataEntryContainer>
      ) : null}
    </>
  );
}

export default Modals;

const ServerErrorText = styled.label`
  position: fixed;
  top: 47%;
  left: 40%;
  font-size: 1rem;
`;

const Buttons = styled.button`
  position: fixed;
  top: 53%;
  left: ${(props) => props.spacing}%;
  padding: 10px 20px;
  border-radius: 10px;
  cursor: pointer;
`;

const DataEntryContainer = styled.div`
  width: 25em;
  height: 10em;
  top: 40%;
  left: 35%;
  border-radius: 20px;
  position: fixed;
  background-color: black;
  z-index: 999;

  h3 {
    padding: 0 50px;
  }
`;
