import React from "react";
import styled from "styled-components";

function Modal({ showModal, setShowModal, errormessage }) {
  return (
    <div>
      <ErrorMessageContainer showModal={showModal}>
        <ServerErrorText>{errormessage}</ServerErrorText>
        <ErrorMessageButton
          onClick={() => {
            ErrorMessageContainer((prev) => !prev);
          }}
        >
          Okay
        </ErrorMessageButton>
      </ErrorMessageContainer>
    </div>
  );
}

export default Modal;

const ServerErrorText = styled.label`
  position: fixed;
  top: 45%;
  left: 40%;
  font-size: 1.5rem;
`;

const ErrorMessageButton = styled.button`
  position: fixed;
  top: 53%;
  left: 56%;
  padding: 10px 20px;
  border-radius: 10px;
  cursor: pointer;
`;

const ErrorMessageContainer = styled.div`
  width: 25em;
  height: 10em;
  top: 40%;
  left: 35%;
  border-radius: 20px;
  position: fixed;
  background-color: whitesmoke;
  z-index: 999;
`;
