import React, { useState, useEffect } from "react";
import "../Styles/Navbar.css";
import { Link } from "react-router-dom";

function Navbar() {
  const [navbar, setNavbar] = useState(false);
  const [mobileview, setMobileView] = useState(true);

  const showMenuBar = () => {
    if (window.innerWidth > 960) {
      setMobileView(false);
    } else {
      setMobileView(true);
    }
  };

  const shrinkNavbar = () => {
    if (window.scrollY > 60) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  useEffect(() => {
    showMenuBar();
  }, []);

  console.log(navbar.valueOf());

  window.addEventListener("resize", showMenuBar);
  window.addEventListener("scroll", shrinkNavbar);

  return (
    <>
      <div className={navbar ? "nav-shrink" : "nav-large"}>
        <div className="navbarContainer">
          <div
            className={
              navbar ? "shrink-title-logo-container" : "title-logo-container"
            }
          >
            <Link to="/random">
              <img src="" alt="logo from db" className="logo" />
            </Link>
            <h5>GES TEXT FROM DB</h5>
            <div
              className={
                navbar ? "navigation-container" : "blank-navigation-container"
              }
            >
              <div className="menu-icon">random</div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Navbar;
