import React from "react";
import styled from "styled-components";

function Modals({ showModals, setShowModals }) {
  return (
    <>
      {showModals ? (
        <ErrorMessageContainer showModal={showModals}>
          <h3>Error</h3>
          <View
            onClick={() => {
              alert("You can view this !");
              //   setShowModals((prev) => !prev);
            }}
          >
            Okay
          </View>
        </ErrorMessageContainer>
      ) : null}
    </>
  );
}

export default Modals;

const ServerErrorText = styled.label`
  position: fixed;
  top: 47%;
  left: 40%;
  font-size: 1rem;
`;

const View = styled.button`
  position: fixed;
  top: 53%;
  left: 56%;
  padding: 10px 20px;
  border-radius: 10px;
  cursor: pointer;
`;

const ErrorMessageContainer = styled.div`
  width: 25em;
  height: 10em;
  top: 40%;
  left: 35%;
  border-radius: 20px;
  position: fixed;
  background-color: whitesmoke;
  z-index: 999;

  h3 {
    padding: 0 50px;
  }
`;
