import React, { useState } from "react";
import { required, email, length } from "../../validator/validator";
import styled from "styled-components";

function AdminLogin() {
  // const [emails, setEmails] = useState({
  // value: "",
  // valid: false,
  // touched: false,
  // validators: [required, email],
  // });
  // const [password, setPassword] = useState({
  // value: "",
  // valid: false,
  // touched: false,
  // validators: [required, length({ min: 5 })],
  // });

  const [form, setForm] = useState({
    loginform: {
      email: {
        value: "",
        valid: false,
        touched: false,
        validators: [required, email],
      },
      password: {
        value: "",
        valid: false,
        touched: false,
        validators: [required, length({ min: 5 })],
      },
      formIsValid: false,
    },
  });

  const inputChangeHandler = (input, value) => {
    setForm((prevState) => {
      let isValid = true;
      for (const validator of prevState.loginform[input].validators) {
        isValid = isValid && validator(value);
      }
      const updateForm = {
        ...prevState.loginform,
        [input]: {
          ...prevState.loginform[input],
          valid: isValid,
          value: value,
        },
      };
      let formIsValid = true;
      for (const inputName in updateForm) {
        formIsValid = formIsValid && updateForm[inputName].valid;
      }
      return {
        loginform: updateForm,
        formIsValid: formIsValid,
      };
    });
  };

  const passValues = (e) => {
    console.log(form.loginform.email, form.loginform.password);
  };
  return (
    <FormContainer onSubmit={passValues}>
      <input
        type="email"
        label="Your Email"
        id="email"
        onChange={inputChangeHandler}
        value={form.loginform["email"].value}
        valid={form.loginform["email"].valid}
        touched={form.loginform["email"].touched}
      />
      <input
        type="password"
        id="password"
        label="Password"
        onChange={inputChangeHandler}
        value={form.loginform["password"].value}
        valid={form.loginform["password"].valid}
        touched={form.loginform["password"].touched}
      />
    </FormContainer>
  );
}

export default AdminLogin;

const ErrorText = styled.label`
  margin-top: 0;
  margin-bottom: 5px;
  color: red;
  text-decoration: none;
  font-weight: 500;
`;

const Text = styled.div`
  color: #3c574e;
  align-items: center;
  justify-content: center;
  display: flex;
  font-size: 1.5rem;
  font-weight: bold;
