import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useFormik } from "formik";
import * as yup from "yup";

function AdminWordsModal({ showModals, setShowModals }) {
  const [designationplaceholder, setDesignationPlaceHolder] = useState("");
  const [descriptionplaceholder, setDescriptionPlaceHolder] = useState("");

  const [textcolor, setTextColor] = useState("");
  const validationSchema = yup.object({
    Designation: yup
      .string()
      .required("Required and must not be less then 3 digit"),
    Description: yup
      .string()
      .required("Required and must not be less than 10 digit"),
    Name: yup
      .string()
      .required("Required and Name must not be less than 2 digit"),
  });

  const onSubmit = (values) => {
    console.log(values);
    alert(values.Designation);
  };

  const formik = useFormik({
    initialValues: { Designation: "", Description: "", Name: "" },
    validateOnBlur: true,
    validationSchema: validationSchema,
    onSubmit,
  });

  // useEffect(() => {
  //   !formik.errors.Designation
  //     ? setDesignationPlaceHolder("Please Enter Your Designation")
  //     : setDesignationPlaceHolder(formik.errors.Designation);

  //   designationplaceholder === "Please Enter Your Designation"
  //     ? setTextColor("red")
  //     : setTextColor("black");
  // }, [formik]);

  // useEffect(() => {
  //   !formik.errors.Description
  //     ? setDescriptionPlaceHolder("Please Enter your Description")
  //     : setDescriptionPlaceHolder(formik.errors.Description);
  // }, [formik]);

  return (
    <>
      {showModals ? (
        <FormContainer onSubmit={formik.handleSubmit}>
          <InputField>
            <LabelText> Designation = </LabelText>
            <InputDescription
              name="Designation"
              type="text"
              placeholder={
                formik.errors.Designation
                  ? formik.errors.Designation
                  : "Please Enter Your Designation"
              }
              color={formik.errors.Designation ? "red" : "black"}
              width={25}
              height={2.5}
              value={formik.values.Designation}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              className={
                formik.errors.Designation &&
                formik.touched.Designation &&
                "error"
              }
            ></InputDescription>
          </InputField>

          {/* <------------------------------------> */}
          <InputField>
            <LabelText> Designation = </LabelText>
            <InputDescription
              name="Description"
              type="text"
              placeholder={
                formik.values.Description
                  ? formik.values.Description
                  : "Please Enter Your Complete Description"
              }
              color={textcolor}
              width={25}
              height={2.5}
              value={formik.values.Description}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              className={
                formik.errors.Description &&
                formik.touched.Description &&
                "error"
              }
            ></InputDescription>
          </InputField>
          <button type="submit">Submit</button>

          <br />
        </FormContainer>
      ) : null}
    </>
  );
}

export default AdminWordsModal;

const FormContainer = styled.form`
  background-color: blue;
  display: flex;
  flex-direction: column;
  width: 40em;
  height: 30em;
  top: 15%;
  left: 30%;
  position: fixed;
`;

const InputField = styled.div`
  display: flex;
  flex-direction: row;
  margin: 5px;
`;

const ErrorText = styled.label`
  margin-top: 0;
  margin-bottom: 5px;
  color: red;
  text-decoration: none;
  font-weight: 500;
  z-index: 999;
`;

const InputDescription = styled.input`
  margin-top: 10px;
  margin-bottom: 10px;
  width: ${(props) => props.width}em;
  height: ${(props) => props.height}em;
  /* width: 20em;
  height: 30px; */
  justify-content: center;
  align-items: center;
  margin-left: 20px;
  border-radius: 15px;
  outline: none;
  border: 1px solid rgba(200, 200, 200, 0.03);
  padding: 0 10px;
  transition: all, 200ms ease-in-out;
  box-sizing: border-box;
  border-bottom: 1.4px solid transparent;

  ::placeholder {
    color: ${(props) => props.color};
  }
  &:focus {
    outline: none;
    border-bottom: 2px solid #111;
  }
  &:not(:last-of-type) {
    border-bottom: 1.4px solid rgba(200, 200, 200, 0.4);
  }
`;

const LabelText = styled.label`
  display: flex;
  flex-direction: column;
  font-size: 1rem;
  color: red;
  justify-content: center;
  margin-left: 10px;
`;
