import React, { useState, useEffect } from "react";
import "../Styles/Navbar.css";
import { Link } from "react-router-dom";
import MenuIcon from "@material-ui/icons/Menu";
import CloseIcon from "@material-ui/icons/Close";

function Navbar() {
  const [navbar, setNavbar] = useState(false);
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [navclassname, setNavClassName] = useState("");

  const shrinkNavbar = () => {
    if (window.scrollY > 60) {
      setNavbar(true);
    } else {
      setNavbar(false);
    }
  };

  const menuBarClick = () => {
    setClick(!click);
  };

  const closeMobileMenu = () => {
    setClick(false);
  };

  useEffect(() => {
    setWidthSize();
  }, []);

  const setWidthSize = () => {
    if (window.innerWidth <= 960) {
      setButton(false);
    } else {
      setButton(true);
    }
  };

  if (navbar) {
    if (button === false) {
      setNavClassName("dropdownmenubar");
    } else {
      setNavClassName("navigationcontainer");
    }
  } else {
    setNavClassName("blank-navigation-container");
  }

  console.log(click);

  window.addEventListener("scroll", shrinkNavbar);
  window.addEventListener("resize", setWidthSize);

  return (
    <>
      <div className={navbar ? "nav-shrink" : "nav-large"}>
        <div className="navbarContainer">
          <div
            className={
              navbar ? "shrink-title-logo-container" : "title-logo-container"
            }
          >
            <Link to="/random">
              <img src="" alt="logo from db" className="logo" />
            </Link>
            <h5>GES TEXT FROM DB</h5>
          </div>

          <ul className={"nav" + $(navclassname)}>
            <li className="navitems">
              <Link to="/aboutus" className="nav-links">
                About us
              </Link>
            </li>
            <li className="navitems">
              <Link to="/aboutus" className="nav-links">
                About us
              </Link>
            </li>
            <li className="navitems">
              <Link to="/aboutus" className="nav-links">
                About us
              </Link>
            </li>
            <li className="navitems">
              <Link to="/aboutus" className="nav-links">
                About us
              </Link>
            </li>
            <li className="navitems">
              <Link to="/aboutus" className="nav-links">
                About us
              </Link>
            </li>
            <li className="navitems">
              <Link to="/aboutus" className="nav-links">
                About us
              </Link>
            </li>
          </ul>
          <div
            className={navbar ? "menuiconcontainer" : "blankmenuiconcontainer"}
          >
            <div className="navmenu" onClick={menuBarClick}>
              <i className="icon">
                {click ? (
                  <CloseIcon fontSize="large" />
                ) : (
                  <MenuIcon fontSize="large" />
                )}
              </i>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Navbar;
