import React, { useState, useRef, useEffect } from "react";
import { getToken, getUser, removeUserSession } from "../../utils/Common";
import styled from "styled-components";
import EntryBox from "../../utils/EntryBox";

function AdminPage(props) {
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState("");
  const [button, setButton] = useState(false);
  const [showModals, setShowModals] = useState(false);

  const fileInputRef = useRef();
  const user = getUser();

  useEffect(() => {
    if (image) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreview(reader.result);
      };
      reader.readAsDataURL(image);
    } else {
      setPreview(null);
    }
  }, [image]);

  const ImageUploadHandler = (event) => {
    event.preventDefault();
    console.log("clicked");
    fileInputRef.current.click();
  };

  const handlesignout = () => {
    removeUserSession();
    props.history.push("/adminlogin");
  };

  const AddPost = () => {
    setShowModals((prev) => !prev);
    setButton(true);
  };

  // useEffect(() => {
  //   button ? EntryBox : null;
  // }, [button]);

  return (
    <div>
      <h1>{user.userId}</h1>
      <h1>Welcome to Admin Page You are Logged In</h1>
      <button onClick={handlesignout}>Sign Out</button>
      <br />
      <br />

      <br />

      <button onClick={AddPost}> Add Post </button>
      {Modals ? (
        <Modals showModals={showModals} setShowModals={setShowModals} />
      ) : null}

      <SchooldetailsContainer>
        {/* <img src={imageselected} alt="unknown" /> */}
        <FormContainer>
          <input
            type="file"
            style={{ display: "none" }}
            alt="img"
            ref={fileInputRef}
            accept="images/*"
            onChange={(event) => {
              const file = event.target.files[0];
              if (file && file.type.substr(0, 5) === "image") {
                setImage(file);
              } else {
                setImage(null);
              }
            }}
          />
          <ImageContainer>
            <Image src={preview} />
          </ImageContainer>
          <Button
            onClick={ImageUploadHandler}
            // onClick={(event) => {
            //   event.preventDefault();
            //   console.log("clicked");
            //   fileInputRef.current.click();
            // }}
          >
            Add Image
          </Button>
        </FormContainer>
      </SchooldetailsContainer>
    </div>
  );
}

export default AdminPage;

const SchooldetailsContainer = styled.div`
  position: fixed;
  top: 10vw;
  left: 10vw;
  height: 20vw;
  width: 20vw;
  background-color: red;
`;

const FormContainer = styled.form`
  margin: 10px;
  height: 18vw;
  background-color: green;
`;

const Image = styled.img`
  height: 10vw;
`;

const ImageContainer = styled.div`
  margin-top: 10px;
  height: 16vw;
  width: 18vw;
`;

const Button = styled.button`
  position: absolute;
  top: 35vh;
  left: 13vw;
`;
