import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useFormik } from "formik";
import * as yup from "yup";

function AdminWordsModal({ showModals, setShowModals }) {
  const [designation, setDesignation] = useState("Designation");
  const [name, setName] = useState("Name");
  const [description, setDescription] = useState("Description");
  const validationSchema = yup.object({
    Designation: yup.string().min(3).required("Designation Can't be empty"),
    Description: yup
      .string()
      .required("Required and must not be less than 10 digit"),
    Name: yup
      .string()
      .min(10)
      .required("Required and Name must not be less than 2 digit"),
  });

  const onSubmit = (values) => {
    console.log(values);
    alert(values.Designation);
  };

  const formik = useFormik({
    initialValues: { Designation: "", Description: "", Name: "" },
    validateOnBlur: true,
    validationSchema: validationSchema,
    onSubmit,
  });

  // formik.errors.Designation
  //   ? setDesignation("Designation Required *")
  //   : setDesignation("Designation");

  // useEffect(() => {
  //   {
  //     if (formik.touched.Designation && formik.errors.Designation) {
  //       setDesignation("Designation Required *");
  //     } else if (formik.touched.Name && formik.errors.Name) {
  //       setName("Name Required * ");
  //     } else if (formik.touched.Description && formik.errors.Description) {
  //       setDescription("Description Required *");
  //     } else {
  //       setDescription("Description");
  //       setDesignation("Designation");
  //       setName("Name");
  //     }
  //   }
  // }, []);

  return (
    <>
      {showModals ? (
        <FormContainer onSubmit={formik.handleSubmit}>
          <InputField>
            <Inputboxandname>
              <LabelText
                type={"small"}
                color={
                  formik.touched.Designation && formik.errors.Designation
                    ? "red"
                    : "black"
                }
                fontsize={
                  formik.touched.Designation && formik.errors.Designation
                    ? 14
                    : 18
                }
              >
                {formik.touched.Designation && formik.errors.Designation
                  ? "Designation Required *"
                  : "Designation"}
              </LabelText>
              <InputDescription
                name="Designation"
                type="text"
                placeholder={
                  formik.errors.Designation && formik.touched.Designation
                    ? formik.errors.Designation
                    : "Please Enter Your Designation"
                }
                color={
                  formik.errors.Designation && formik.touched.Designation
                    ? "red"
                    : "black"
                }
                width={15}
                height={2.5}
                margin_left={20}
                type={"random"}
                value={formik.values.Designation}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                className={
                  formik.errors.Designation &&
                  formik.touched.Designation &&
                  "error"
                }
              ></InputDescription>
            </Inputboxandname>

            {/* <---------------------- Name ------------------->  */}
            <Inputboxandname>
              <LabelText
                type={"small"}
                color={
                  formik.touched.Name && formik.errors.Name ? "red" : "black"
                }
              >
                {formik.touched.Name && formik.errors.Name
                  ? "Name Required *"
                  : "Name"}
              </LabelText>
              {/* <h1>{formik.errors.Name}</h1> */}
              <InputDescription
                name="Name"
                type="text"
                placeholder={
                  formik.errors.Name && formik.touched.Name
                    ? formik.errors.Name
                    : "Please Enter Your Name"
                }
                color={
                  formik.errors.Name && formik.touched.Name ? "red" : "black"
                }
                width={15}
                height={2.5}
                margin_left={20}
                type={"random"}
                value={formik.values.Name}
                onChange={formik.handleChange}
                onBlur={formik.handleBlur}
                className={
                  formik.errors.Designation &&
                  formik.touched.Designation &&
                  "error"
                }
              ></InputDescription>
            </Inputboxandname>
          </InputField>

          {/* <---------------------- Description ------------------->  */}

          <InputField>
            <LabelText
              type={"random"}
              color={
                formik.touched.Description && formik.errors.Description
                  ? "red"
                  : "black"
              }
            >
              {formik.touched.Description && formik.errors.Description
                ? "Description Required *"
                : "Description"}
            </LabelText>
            <MultipleLineInput
              name="Description"
              type="textarea"
              placeholder={
                formik.errors.Description && formik.touched.Description
                  ? formik.errors.Description
                  : "Please Enter Your Complete Description"
              }
              color={
                formik.errors.Description && formik.touched.Description
                  ? "red"
                  : "black"
              }
              cols="60"
              rows="16"
              value={formik.values.Description}
              onChange={formik.handleChange}
              onBlur={formik.handleBlur}
              className={
                formik.errors.Description &&
                formik.touched.Description &&
                "errors"
              }
            >
              NoResize
            </MultipleLineInput>
          </InputField>
          <button type="submit">Submit</button>

          <br />
        </FormContainer>
      ) : null}
    </>
  );
}

export default AdminWordsModal;

const FormContainer = styled.form`
  background-color: #001219;
  display: flex;
  flex-direction: column;
  width: 40em;
  height: 30em;
  top: 15%;
  left: 30%;
  position: fixed;
  border-radius: 20px;
  justify-content: space-evenly;
`;

const InputField = styled.div`
  display: flex;
  flex-direction: row;
  padding: 5px;
  justify-content: space-evenly;
`;

const Inputboxandname = styled.div`
  display: flex;
  flex-direction: column;
`;

const ErrorText = styled.label`
  margin-top: 0;
  margin-bottom: 5px;
  color: red;
  text-decoration: none;
  font-weight: 500;
  z-index: 999;
`;

const InputDescription = styled.input`
  width: ${(props) => props.width}em;
  height: ${(props) => props.height}em;
  text-justify: auto;
  text-align: start;
  font-family: "Roboto", sans-serif;
  margin-left: ${(props) => props.margin_left}px;
  border-radius: 15px;
  outline: none;
  border: 1px solid rgba(200, 200, 200, 0.03);
  padding: 0 10px;
  transition: all, 200ms ease-in-out;
  box-sizing: border-box;
  border-bottom: 1.4px solid transparent;

  ::placeholder {
    color: ${(props) => props.color};
  }
  &:focus {
    outline: none;
    border-bottom: 2px solid #111;
  }
  &:not(:last-of-type) {
    border-bottom: 1.4px solid rgba(200, 200, 200, 0.4);
  }
`;

const MultipleLineInput = styled.textarea`
  resize: none;
  text-justify: auto;
  font-family: "Roboto", sans-serif;
  text-align: start;
  margin-left: 10px;
  margin-right: 10px;
  border-radius: 15px;
  outline: none;
  border: 1px solid rgba(200, 200, 200, 0.03);
  padding: 0 10px;
  transition: all, 200ms ease-in-out;
  box-sizing: border-box;
  border-bottom: 1.4px solid transparent;
  padding: 10px;
  text-indent: 10px;
  ::placeholder {
    color: ${(props) => props.color};
    text-indent: 10px;
  }
  &:focus {
    outline: none;
    border-bottom: 2px solid #111;
  }
  &:not(:last-of-type) {
    border-bottom: 1.4px solid rgba(200, 200, 200, 0.4);
  }
`;

const LabelText = styled.label`
  display: flex;
  flex-direction: column;
  font-size: ${(props) => props.fontsize}px;
  color: ${(props) => props.color};
  justify-content: ${(props) =>
    props.type === "small" ? `center` : `flex-start`};
  margin-top: ${(props) => (props.type === "small" ? -2 : 8)}px;
  margin-left: 18px;
  margin-bottom: 5px;
`;
