// update school details;
export const updateSchoolName = (details) => {
  return {
    type: "UPDATE_SCHOOLNAME",
    payload: details,
  };
};

export const updateSchoollogo = (logo) => {
  return {
    type: "UPDATE_SCHOOLLOGO",
    payload: logo,
  };
};
